mapper.insert:
INSERT INTO student_enrollment (
    user_id, notice_number, id_number, report_date, leave_request, enrollment_status, file_name
) VALUES (
             #{userId}, #{noticeNumber}, #{idNumber}, #{reportDate}, #{leaveRequest}, #{enrollmentStatus}, #{fileName}
         )

mapper.update:
UPDATE student_enrollment SET
                              leave_request = #{leaveRequest},
                              enrollment_status = #{enrollmentStatus}
WHERE enrollment_id = #{enrollmentId}

UPDATE student_enrollment SET
                              notice_number = #{noticeNumber},
                              id_number = #{idNumber},
                              file_name = #{fileName}
WHERE enrollment_id = #{enrollmentId}
